<template>    <div class="banner">        <h1>input</h1>        <pre class="input">npm install vue-</pre>        <h1>demo</h1>        <div class="info_banner_container">            <con_icon_container_five>                <ul class="icon_ul">                    <li class="icon_li" v-for="item in itemIcon">                        <con_icon_five :itemProp="item"></con_icon_five>                    </li>                </ul>            </con_icon_container_five>        </div>    </div></template><script>    import reqDataConfig from '../commonjs/no_scene_req_data_config'    import fetch from '../commonjs/fetch'    import {getIconData} from "../conService/banner/service";  // 单独引入    export default {        name:'banner',        data:function(){            return {                items:[],                itemIcon:[],                url:"",                reqData:{},    //发给后端的数据                reqType:'get',                pageIndex:0,  //请求页数                pageSize:0,            }        },        created(){            let self = this;            self.adjustParam();  // 初始化参数配置            self.getDataStart();  // 先获取位置        },        methods:{            adjustParam: function(){  // 调整和配置页面请求的参数                let self = this;                let reqDConfig = reqDataConfig.page_info_icon_config;                self.url = reqDConfig.url;                self.reqData = reqDConfig.reqData;                self.pageIndex = self.reqData.page_index;  //初始化pageIndex                self.pageSize = self.reqData.page_size;                self.reqType = reqDConfig.type;                self.addUrlParams();            },            getDataStart: async function(){  //初始打开页面加载数据                let self = this;                let res;  //请求返回内容                self.reqData.page_index = 0;                self.pageIndex = 0;                self.items = [];   // 必须有，涉及到vue的数据和视图绑定更新的问题                const url = "https://h5.dianping.com/app/adunion-newjh/index.html#/page_info_group_slit";                self.itemIcon = await getIconData(url, self);                try{                    res = await fetch(self.url, self.reqType, self.reqData);                    if(res.shop_info.length < self.pageSize){                        self.isShow = true;                    }                    if(res.status === 0){                        for(let i = 0, len = res.shop_info.length; i < len; i++){                            res.shop_info[i].pos = self.listPos;                            self.listPos++;                        }                        self.items = self.items.concat(res.shop_info);                    }else{                        self.isShow = true;                        //console.log(res.msg)                    }                }catch(error){                    throw("getDataStart函数获取getshoplist数据错误" + error);                }            },            addUrlParams:function(){                let self = this;                // 兼容utm插入到中间的问题                "cat2_id|sub_media|slotId|slotID|cityId|cat1ID|timestamp|lat|lng|deviceID|deviceId|media|mainsecondcategory|shoptype|fh|utm|utm_source|needcheck|info_type|channelId|utmSourceId|needFirstDeepLink".split('|').forEach(function (item) {   //cat0ID|referRequestID|referPlatformID|lng|lat|                    let val = self.getQueryString(item);                    if(val && val !== '*'){                        self.reqData[item] = val;                    }                });            },            getQueryString: function (name) {                let reg = new RegExp("(^|&)" + name + "=([^&]*)(&|$)", "i");                let r = window.location.search.substr(1).match(reg);                if (r !== null) return r[2]; return null;            }        },        props:{        },        computed:{        },        components:{}    }</script><style lang="stylus" scoped>    .banner        font-size:0.1rem;    .info_banner_container        font-size:0.1rem;        border:1px solid #f63;    .info_ul        margin-top:0.04rem;    .info_list        position:relative;    .icon_ul        width:100%;        height:100%;        position:relative;        display:flex;        flex-wrap:wrap;        flex-direction:row;        justify-content:space-between;        align-content:space-between;        .icon_li   // 控制每一个icon的占位比例            position:relative;            width:18%;            height:100%;</style>